// Amiri-Regular-normal.js
var amiriRegularBase64 = "..."; // Base64 string of the TTF file

(function (jsPDFAPI) {
   
    if (typeof jsPDF !== 'undefined') {
        jsPDFAPI.addFileToVFS("Amiri-Regular.ttf", amiriRegularBase64);
        jsPDFAPI.addFont("Amiri-Regular.ttf", "Amiri", "normal");
    }
})(window.jspdf.API);
